      <script async src="./assets/main.js"></script>
	  <script type="text/javascript" src="./js/utils/cookie.js"></script>
	  <script type="text/javascript" src="./js/config.js"></script>
	  <script type="text/javascript" src="./js/utils/url.js"></script>
	  <script type="text/javascript" src="./js/functions.js"></script>
	  <script type="text/javascript">

		var error_div = document.getElementById('error');
		
		var usp = new URLSearchParamsPolyfill(window.location.search);
			
		var nvic = usp.get('nvic');
		var suv = usp.get('sid');	
		
		if((nvic != null && nvic == 'new') && (suv != null && suv != '')) {
			
			checkServerSuv(suv);
			
			var url = `${api_url}/character/check`;
			var jwt_hash = getCookie('jwt_authentication_hash');

			var xhr = new XMLHttpRequest();

			xhr.open('GET', url, true);
			xhr.setRequestHeader('Content-type', 'application/json');	
			xhr.setRequestHeader('Authorization', `Bearer ${jwt_hash}`);

			xhr.onreadystatechange = function() {
			  if(xhr.readyState == 4) {
				if(xhr.status == 200) {
				  var response = JSON.parse(xhr.responseText);
				  if(response.character_is_created == true) {
					window.location.href = '/selectserver';
				  }
				} else {
				  console.log("Erro na solicitao. Cdigo do status: " + xhr.status);
				}						
			  }
			};

			xhr.send();				
		} 
		
		if((nvic != null && nvic == 'new') && suv == null) {
			window.location.href = '/selectserver?error_code=1';
		} else if(nvic != null && nvic != 'new') {
			window.location.href = '/';
		}

		document.getElementById('newuser').addEventListener('click', function(event) {
			event.preventDefault();
			
			var nickname = document.getElementById('register_nickname').value.trim();
			var gender = document.getElementById('register_sex').value.trim();
			var captchaChallenge = document.getElementById('captchaResult').value.trim();
			
			if(nickname == '' || gender == '' || captchaChallenge == '') {
				displayMessage(type = 'error', message = 'Voc no preencheu todos os campos solicitados.');
			} else if (nickname.length < 3 || nickname.length > 16) {
				displayMessage(type = 'error', message = 'O nome do personagem deve ter entre 3 a 16 caracteres...');
			} else if(captchaChallenge !== getCookie('captchaResult')) {
				displayMessage(type = 'error', message = 'A resposta do cdigo est errada tente novamente.');
			} else {
				var url = `${api_url}/character/create/${suv}`;
				var params = `nickname=${nickname}&gender=${gender}`;
				var jwt_hash = getCookie('jwt_authentication_hash');
				
				var xhr = new XMLHttpRequest();
				
				xhr.open('POST', url, true);
				xhr.setRequestHeader('Content-type', 'application/x-www-form-urlencoded');
				xhr.setRequestHeader('Content-type', 'application/json');
				xhr.setRequestHeader('Authorization', `Bearer ${jwt_hash}`);
				
				xhr.onreadystatechange = function() {
					if(xhr.readyState == 4) {
						if(xhr.status == 200) {
							var response = JSON.parse(xhr.responseText);
							if(response.success == true) {
								displayMessage(type = 'success', message = response.message);
								setTimeout(function(){
									window.location.href = `/serverlist?suv=${suv}`;
								}, 1000);
							} else {
								displayMessage(type = 'error', message = response.message);
							}
						} else if(xhr.status == 401) {
							displayMessage(type = 'error', message = 'A sesso expirou, faa o login novamente.');
							setTimeout(function(){
								window.location.href = '/selectserver?logout=true';
							}, 1000);
						} else {
							console.log("Erro na solicitao. Cdigo do status: " + xhr.status);
						}						
					}
				};
				
				xhr.send(params);
			}
		});
		
		if(usp.get('error_code') != null) {
			
			var error_code = usp.get('error_code');
			
			switch(error_code) {
				case '1': 
					document.getElementById('alert_message').innerHTML = `<div class='alert alert-danger ocult-time'>O servidor que voc tentou acessar no existe ou est restrito para um pequeno nmero de pessoas.</div>`;
					break;
				case '2': 
					document.getElementById('alert_message').innerHTML = `<div class='alert alert-danger ocult-time'>Sua conta foi suspensa, entre em contato com o suporte para obter mais informaes.</div>`;
					break;
			}
		}			
	  </script>
   </body>
